const ItemCount = (props) => {
    let count = 0;

    const increment = () => {
        count = count ++ ;
        console.log(count);
    }

    const decrease = () => {
        count = count --;
        console.log(count);
    }

    return (
        <div className="count-container">
            <h3>Contador</h3>
            <p>{count}</p>
            <button onClick={increment}>-</button>
            <button onClick={decrease}>+</button>
        </div>
    )
}

export default ItemCount;
